import React, { useState } from 'react';
import axios from 'axios';

function LendForm() {
  const [formData, setFormData] = useState({
    customerName: '',
    principal: '',
    interestRate: '',
    years: ''
  });
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/lend', {
        customerName: formData.customerName,
        principal: parseFloat(formData.principal),
        interestRate: parseFloat(formData.interestRate),
        years: parseInt(formData.years)
      });
      setMessage(`Loan created! EMI: $${response.data.emi}`);
      setFormData({ customerName: '', principal: '', interestRate: '', years: '' });
    } catch (error) {
      setMessage(error.response?.data?.error || 'Error creating loan');
    }
  };

  return (
    <div className="max-w-md mx-auto bg-white p-6 rounded-lg shadow">
      <h2 className="text-2xl font-bold mb-4">Create New Loan</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label className="block text-sm font-medium mb-1">Customer Name</label>
          <input
            type="text"
            value={formData.customerName}
            onChange={(e) => setFormData({...formData, customerName: e.target.value})}
            className="w-full p-2 border rounded"
            required
          />
        </div>
        <div>
          <label className="block text-sm font-medium mb-1">Principal Amount ($)</label>
          <input
            type="number"
            value={formData.principal}
            onChange={(e) => setFormData({...formData, principal: e.target.value})}
            className="w-full p-2 border rounded"
            required
          />
        </div>
        <div>
          <label className="block text-sm font-medium mb-1">Interest Rate (%)</label>
          <input
            type="number"
            step="0.1"
            value={formData.interestRate}
            onChange={(e) => setFormData({...formData, interestRate: e.target.value})}
            className="w-full p-2 border rounded"
            required
          />
        </div>
        <div>
          <label className="block text-sm font-medium mb-1">Duration (Years)</label>
          <input
            type="number"
            value={formData.years}
            onChange={(e) => setFormData({...formData, years: e.target.value})}
            className="w-full p-2 border rounded"
            required
          />
        </div>
        <button type="submit" className="w-full bg-blue-600 text-white p-2 rounded hover:bg-blue-700">
          Create Loan
        </button>
      </form>
      {message && <p className="mt-4 text-center text-green-600">{message}</p>}
    </div>
  );
}

export default LendForm;
